function run(response) {
	try {
		response = JSON.parse(response);
		if (response.length == 5) {
			const filteredRepos = response.map(repo => ({
				avatarUrl: repo.owner.avatar_url,
				fullName: repo.full_name,
				description: repo.description
			}));

			return makeCarouselMenu(filteredRepos);
		}
	} catch (error) {
		return "";
	}
}

function makeCarouselMenu(data) {
	const items = data.map(d => (
		{
			"header": {
				"type": "application/vnd.lime.media-link+json",
				"value": {
					"title": d.fullName,
					"text": d.description,
					"type": "image/jpeg",
					"uri": d.avatarUrl
				}
			},
			"options": []
		}
	));

	let content = {
		"itemType": "application/vnd.lime.document-select+json",
		"items": items
	};

	return {
		"type": "application/vnd.lime.collection+json",
		"content": content
	}
}

function run(inputContent) {
    inputContent = inputContent
        .replace(/\s*/, " ")
        .trim();

    const menuOptions = {
        "^(💙 Be)$": "Take.Be",
        "^(🏳 Team)$": "Take.Team",
        "^(⭐ Excellence)$": "Take.Excellence",
        "^(⚡ Charge)$": "Take.Charge",
        "^(🎯 Simple)$": "Take.Simple",
        "^(🚀 Higher)$": "Take.Higher",
        "^(💡 Desafio)$": "Desafio"
    };

    const foundOption = Object.entries(menuOptions).find(([regexPattern]) => {
        const regex = new RegExp(regexPattern, 'gmi');
        return regex.test(inputContent);
    });

    return foundOption ? foundOption[1] : "Input inesperado";
}

